# ╔═╡ c0798447-410f-426e-b9c6-5e52cf522a84
function bWeight(E, T)
	return exp(-E / T)
end

# ╔═╡ 87adc4fc-8cda-11eb-3de4-1d0265ce4226
function G_rule(deltaE, T)
	W = bWeight(deltaE, T)
	if (W == Inf)
		return 1
	else
		return W / (1 + W)
	end
end

