# ╔═╡ 897b2232-6add-46d2-b0af-d9fbbc87524f
function tanhT(deltaE, T)
	return 0.5 * (1 - tanh(deltaE / (2 * T)))
end

# ╔═╡ 7905af69-ffcb-490b-a273-4e7573ee6324
function mytanh(x)
	px = exp(x)
	mx = exp(-x)
	return (px - mx) / (px + mx)
end

# ╔═╡ 64457d45-d43a-4034-b15b-eb54ee641456
function nottanh(x)
	mx = exp(-x)
	return mx / (1 + mx)
end

