# ╔═╡ 8a64642c-6d5e-485f-85a6-103144cc05ce
function samplingHistogram(setSize, samples, maxbin)
	theSet = zeros(Int, setSize)
	hist = zeros(Int, maxbin + 1)
	for i in 1:samples
		x = rand(1:setSize)
		theSet[x] += 1
	end
	for j in 1:setSize
		count = theSet[j]
		if count <= maxbin
			hist[count+1] += 1
		end
	end
	return hist
end

